const CoursePart = ({part}) => <p>{part.name} {part.exercises}</p>

const CourseContent = ({courseParts}) => (
  <>
    {courseParts.map(part => 
      <CoursePart key={part.id} part={part} />
    )}
  </>
)

const Course = ({course}) => {
  const sum = course.parts.reduce(
    (accumulator, currentValue) => accumulator + currentValue.exercises, 0
  )
  return (
    <>
      <h1>{course.name}</h1>
      <CourseContent courseParts={course.parts} />
      <p>total of {sum} exercises</p>
    </>
  )
}

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return (
    <div>
      {courses.map(course =>
        <Course key={course.key} course={course} />)}
    </div>
  )
}

export default App
