const CoursePart = ({part}) => <p>{part.name} {part.exercises}</p>

const CourseContent = ({courseParts}) => (
  <>
    {courseParts.map(part => 
      <CoursePart key={part.id} part={part} />
    )}
  </>
)

const Course = ({course}) => {
  const sum = course.parts.reduce(
    (accumulator, currentValue) => accumulator + currentValue.exercises, 0
  )
  return (
    <>
      <h3>{course.name}</h3>
      <CourseContent courseParts={course.parts} />
      <p>total of {sum} exercises</p>
    </>
  )
}
export default Course
